const mongoose = require("mongoose");
const jobSchema = new mongoose.Schema({
  title: {
    type: String,
    required: true,
  },
  company: {
    type: String,
    required: true,
  },
  location: {
    type: String,
    required: true,
  },
  companyLogo: {
    type: String,
    default:
      "data:image/png;base64,",
  },
  jobType: {
    type: String,
    enum: ["Full-Time", "Part-Time", "Internship", "Contract"],
    required: true,
  },
  requirements: {
    type: [String],
    required: true,
  },
  experienceRequired: {
    type: String,
    required: true,
    default: 0,
  },
  salaryRange: {
    type: String,
  },
  postedDate: {
    type: Date,
    default: Date.now,
  },
  applicationDeadline: {
    type: Date,
  },
  employer: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Employer",
  },
  description: {
    type: String,
    required: true,
  },
});
const Job = mongoose.model("Job", jobSchema);
module.exports = { Job };
